import React, { useState } from "react";

function App() {
  const [inputValue, setInputValue] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      handleSearch();
    }
  };

  const handleSearch = () => {
    const formattedInput = inputValue.trim().toLowerCase();
    fetch("data.txt")
      .then((response) => response.text())
      .then((data) => {
        const lines = data.split("\n");
        const results = [];

        lines.forEach((line) => {
          const formattedLine = line.trim().toLowerCase();
          if (formattedLine.includes(formattedInput)) {
            results.push(formattedLine);
          }
        });
        ////////////// Start of Test Area /////////////////
        let aseLineForDisplay;
        for (let i = 0; i < results.length; i++) {
          const lineByLine = results[i];
          // console.log(`Line By Line ${lineByLine} ---- end of lineByline -----`);

          const wordsArray = lineByLine.split("\t");
          const wordBeforeTab = wordsArray[0];
          aseLineForDisplay = lineByLine;
          // console.log(
          //   `wordBeforeTab this inside in for loop ${wordBeforeTab} --- end of wordBeforeTab `
          // ); // Output: "anew"
          if (wordBeforeTab === formattedInput) {
            // console.log("The strings are equal");

            // console.log(`This is line 310 ${aseLineForDisplay}`);
            const indexOfSearchElement = results.indexOf(aseLineForDisplay);
            //now remove  search elemelement
            results.splice(indexOfSearchElement, 1);

            break;
          } else {
            aseLineForDisplay = `We haven't this word.But We can add it for you.Simply,click the "Contribute" button below`;
            // console.log("The strings are not equal");
          }

          // Process the line here...
        }
        ////////////// End of Test Area //////////////////
        results.sort((a, b) => {
          if (a === formattedInput) {
            return -1;
          }
          if (b === formattedInput) {
            return 1;
          }
          return a.localeCompare(b);
        });
        setSearchResults(results.push(aseLineForDisplay));
      })
      .catch((error) => console.log(error));
  };

  return (
    <div className="container-md shadow-lg rounded-4 position-absolute top-50 start-50 translate-middle bg-white p-0">
      <div className="container">
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Search"
            aria-label="Search"
            aria-describedby="button-addon2"
            value={inputValue}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
          />
          <button
            className="btn btn-outline-secondary"
            type="button"
            id="button-addon2"
            onClick={handleSearch}
          >
            Search
          </button>
        </div>
        <ul className="list-group">
          {searchResults.map((result, index) => (
            <div>
              <li className="list-group-item" key={index + 1}>
                {result}
              </li>
            </div>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
