import React, { useState } from "react";
import axios from "axios";

function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = async (event) => {
    event.preventDefault();
    const formattedSearchTerm = searchTerm.trim().toLowerCase();
    const { data } = await axios.get("/data.txt");

    const filteredData = data.split("\n").filter((line) => {
      const formattedLine = line.trim().toLowerCase();
      return formattedLine.includes(formattedSearchTerm);
    });

    if (filteredData.length === 0) {
      alert("No matching results found.");
      return;
    }

    const sortedResults = [
      formattedSearchTerm,
      ...filteredData.filter((line) => line !== formattedSearchTerm),
    ];
    setSearchResults(sortedResults);
  };

  return (
    <div>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={searchTerm}
          onChange={(event) => setSearchTerm(event.target.value)}
        />
        <button type="submit">Search</button>
      </form>

      {searchResults.length > 0 && (
        <ul>
          {searchResults.map((result) => (
            <li key={result}>{result}</li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
