import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [data, setData] = useState([]);
  const [userInput, setUserInput] = useState("");
  const [filteredData, setFilteredData] = useState([]);

  useEffect(() => {
    axios.get("/data.txt").then((response) => {
      setData(response.data.split("\n"));
    });
  }, []);

  function handleSearch() {
    const formattedUserInput = userInput.toLowerCase().trim();
    const matchedWords = data.filter((word) =>
      word.toLowerCase().includes(formattedUserInput)
    );

    if (matchedWords.length === 0) {
      setFilteredData([]);
      return;
    }

    const exactMatchIndex = matchedWords.findIndex(
      (word) => word.toLowerCase() === formattedUserInput
    );

    if (exactMatchIndex > -1) {
      matchedWords.unshift(matchedWords.splice(exactMatchIndex, 1)[0]);
    }

    setFilteredData(matchedWords);
  }

  function handleInputChange(event) {
    setUserInput(event.target.value);
  }

  return (
    <div>
      <div>
        <input type="text" value={userInput} onChange={handleInputChange} />
        <button onClick={handleSearch}>Searchs</button>
      </div>
      <div>
        {filteredData.length === 0 ? (
          <div>No results found</div>
        ) : (
          <ul>
            {filteredData.map((word) => (
              <li key={word}>{word}</li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default App;
