import React, { useState } from "react";

function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = () => {
    fetch("data.txt")
      .then((response) => response.text())
      .then((text) => {
        const lines = text.split("\n");
        const results = [];

        for (let i = 0; i < lines.length; i++) {
          if (lines[i].startsWith(searchTerm)) {
            results.push(
              <li key={i}>
                {lines[i].replace(searchTerm, `<strong>${searchTerm}</strong>`)}
              </li>
            );
          }
        }

        setSearchResults(results);
      });
  };

  return (
    <div className="container-md shadow-lg rounded-4 position-absolute top-50 start-50 translate-middle bg-white p-0">
      <div className="container">
        <div className="d-flex justify-content-between">
          <div className="align-self-start my-2">
            <h1 className="fw-bold fs-1 text-uppercase mb-0">
              Sinhala-English Dictionary
            </h1>
            <span className="text-muted fs-5">By Hasindu Rangika</span>
          </div>
          <div className="align-self-end my-2">
            <input
              type="text"
              className="form-control me-2"
              placeholder="Search for a word"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <button
              className="btn btn-primary"
              type="button"
              id="button-addon2"
              onClick={handleSearch}
            >
              Search
            </button>
          </div>
        </div>
        <hr className="my-0" />
        <div className="my-3">
          {searchResults.length > 0 ? (
            <ul>{searchResults}</ul>
          ) : (
            <p className="text-muted">No results found.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
