import React, { useState } from "react";
import axios from "axios";

function App() {
  const [searchInput, setSearchInput] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleInputChange = (event) => {
    setSearchInput(event.target.value);
  };

  const handleSearchButtonClick = async () => {
    const formattedSearchInput = searchInput.trim().toLowerCase();
    try {
      const response = await axios.get("/Data.txt");
      const data = response.data
        .split("\n")
        .map((line) => line.trim().toLowerCase());
      const searchResults = data.filter((word) =>
        word.includes(formattedSearchInput)
      );
      if (searchResults.length > 0) {
        const formattedSearchResults = searchResults.sort((a, b) => {
          if (a.startsWith(formattedSearchInput)) {
            return -1;
          } else if (b.startsWith(formattedSearchInput)) {
            return 1;
          } else {
            return 0;
          }
        });
        setSearchResults(formattedSearchResults);
      } else {
        setSearchResults(["No results found."]);
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSearchButtonClick();
    }
  };

  return (
    <div>
      <div>
        <input
          type="text"
          value={searchInput}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
        />
        <button onClick={handleSearchButtonClick}>Search</button>
      </div>
      <div>
        {searchResults.map((result) => (
          <div key={result}>{result}</div>
        ))}
      </div>
    </div>
  );
}

export default App;
