import React, { useState } from "react";
import axios from "axios";

function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [error, setError] = useState("");

  const handleSearch = async () => {
    // format the search term
    const formattedSearchTerm = searchTerm.trim().toLowerCase();

    // fetch the data from the file
    try {
      const response = await axios.get("data.txt");
      const lines = response.data.split("\n");
      const results = [];

      // loop through the lines and find matches
      for (let i = 0; i < lines.length; i++) {
        const line = lines[i].trim().toLowerCase();
        if (line.includes(formattedSearchTerm)) {
          results.push(line);
        }
      }

      // find the index of the line that contains "new"
      const newLineIndex = results.findIndex((line) =>
        line.includes(formattedSearchTerm)
      );

      // move the new line to the front of the array
      if (newLineIndex !== -1) {
        const newLine = results.splice(newLineIndex, 1)[0];
        results.unshift(newLine);
      }

      // update the state with the results
      setSearchResults(results);
      setError("");
    } catch (err) {
      setError("Error loading data");
      setSearchResults([]);
    }
  };

  return (
    <div>
      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        onKeyPress={(e) => {
          if (e.key === "Enter") {
            handleSearch();
          }
        }}
      />
      <button onClick={handleSearch}>Search</button>
      {error ? <div>{error}</div> : null}
      {searchResults.length > 0 ? (
        <ul>
          {searchResults.map((result, index) => (
            <li key={index}>{result}</li>
          ))}
        </ul>
      ) : null}
    </div>
  );
}

export default App;
