import React, { useState } from "react";
import "./App.css";

function App() {
  const [searchWord, setSearchWord] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleInputChange = (event) => {
    setSearchWord(event.target.value);
  };

  const handleSearch = () => {
    fetch("data.txt")
      .then((response) => response.text())
      .then((data) => {
        const results = data
          .split("\n")
          .filter((line) => line.includes(searchWord))
          .map((line) => {
            const [word, definition] = line.split(":");
            return (
              <li key={word}>
                <strong>{word}</strong>: {definition}
              </li>
            );
          });
        setSearchResults(results);
      });
  };

  return (
    <div className="main-container">
      <div className="search-container shadow-lg rounded-3">
        <label>
          <input
            type="text"
            value={searchWord}
            onChange={handleInputChange}
            placeholder="Enter Word"
            className="form-control rounded-3"
          />
        </label>
        <button
          className="btn btn-success rounded-3"
          onClick={handleSearch}
          style={{ boxShadow: "0 4px 4px rgba(0,0,0,0.4)" }}
        >
          Search
        </button>
        <ul className="search-results">{searchResults}</ul>
      </div>
      <div className="buttons-container">
        <button className="btn btn-outline-success">About</button>
        <button className="btn btn-outline-secondary">Contribute</button>
        <button className="btn btn-outline-secondary">Contact</button>
      </div>
    </div>
  );
}

export default App;
