import React, { useState } from "react";
import axios from "axios";

function App() {
  const [inputValue, setInputValue] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    const cleanedInputValue = inputValue.trim().toLowerCase();
    axios.get("/data.txt").then((response) => {
      const lines = response.data.split("\n");
      const matchingLines = lines.filter((line) =>
        line.toLowerCase().includes(cleanedInputValue)
      );
      if (matchingLines.length === 0) {
        setSearchResults(["No results found"]);
      } else {
        const formattedResults = matchingLines.map((line) =>
          line.trim().toLowerCase()
        );
        const sortedResults = [
          formattedResults.find((result) => result === cleanedInputValue),
          ...formattedResults.filter((result) => result !== cleanedInputValue),
        ];
        setSearchResults(sortedResults);
      }
    });
  };

  return (
    <div>
      <form onSubmit={handleFormSubmit}>
        <input type="text" value={inputValue} onChange={handleInputChange} />
        <button type="submit">Search</button>
      </form>
      <div>
        {searchResults.map((result, index) => (
          <p key={index}>{result}</p>
        ))}
      </div>
    </div>
  );
}

export default App;
