import React, { useState } from "react";
import axios from "axios";

function SearchBar() {
  const [input, setInput] = useState("");
  const [results, setResults] = useState([]);

  const handleSearch = async () => {
    const formattedInput = input.toLowerCase().trim();
    const response = await axios.get("/Data.txt");
    const data = response.data.split("\n");
    const filteredData = data.filter((line) =>
      line.toLowerCase().trim().includes(formattedInput)
    );
    const sortedData = [
      filteredData.find((line) => line.toLowerCase().trim() === formattedInput),
      ...filteredData.filter(
        (line) => line.toLowerCase().trim() !== formattedInput
      ),
    ];
    setResults(sortedData);
  };

  const handleInput = (event) => {
    setInput(event.target.value);
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSearch();
    }
  };

  return (
    <div>
      <input
        type="text"
        value={input}
        onChange={handleInput}
        onKeyPress={handleKeyPress}
      />
      <button onClick={handleSearch}>Search</button>
      {results.length === 0 ? (
        <div>No results found.</div>
      ) : (
        <div>
          {results.map((line, index) => (
            <div key={index}>{line}</div>
          ))}
        </div>
      )}
    </div>
  );
}

export default SearchBar;
