import React, { useState } from "react";

function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchClick = (event) => {
    event.preventDefault();
    search();
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      search();
    }
  };

  const search = () => {
    // Format search term
    const formattedSearchTerm = searchTerm.trim().toLowerCase();

    // Read data file
    fetch("data.txt")
      .then((response) => response.text())
      .then((data) => {
        // Split data into lines
        const lines = data.split("\n");

        // Filter lines that match search term
        const matchingLines = lines.filter((line) =>
          line.trim().toLowerCase().includes(formattedSearchTerm)
        );

        // Sort matching lines so that exact matches appear first
        matchingLines.sort((line1, line2) => {
          if (line1.trim().toLowerCase() === formattedSearchTerm) {
            return -1;
          } else if (line2.trim().toLowerCase() === formattedSearchTerm) {
            return 1;
          } else {
            return 0;
          }
        });

        // Update search results
        setSearchResults(matchingLines);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div className="container my-4">
      <form className="mb-3">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Enter a word"
            value={searchTerm}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
          />
          <button
            className="btn btn-primary"
            type="button"
            id="button-addon2"
            onClick={handleSearchClick}
          >
            Search
          </button>
        </div>
      </form>
      {searchResults.length > 0 && (
        <div className="card">
          <div className="card-header">Search Results</div>
          <ul className="list-group list-group-flush">
            {searchResults.map((line, index) => (
              <li
                className={`list-group-item ${index === 0 ? "fw-bold" : ""}`}
                key={index}
              >
                {line}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
