import React, { useState } from "react";

const App = () => {
  const [inputValue, setInputValue] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleSearch = async (event) => {
    event.preventDefault();

    const formattedInputValue = inputValue.trim().toLowerCase();
    const response = await fetch("data.txt");
    const data = await response.text();
    const lines = data.split("\n");
    const matchingLines = lines.filter((line) =>
      line.trim().toLowerCase().includes(formattedInputValue)
    );
    if (matchingLines.length > 0) {
      const formattedResults = matchingLines.map((line) =>
        line.trim().toLowerCase()
      );
      const firstResultIndex = formattedResults.findIndex(
        (result) => result === formattedInputValue
      );
      const firstResult =
        firstResultIndex !== -1
          ? formattedResults.splice(firstResultIndex, 1)[0]
          : formattedResults.shift();
      setSearchResults([firstResult, ...formattedResults]);
    } else {
      setSearchResults(["Error: Word not found."]);
    }
  };

  return (
    <div>
      <form onSubmit={handleSearch}>
        <label htmlFor="searchInput">Search:</label>
        <input
          type="text"
          id="searchInput"
          value={inputValue}
          onChange={handleInputChange}
        />
        <button type="submit">Search</button>
      </form>
      {searchResults.length > 0 && (
        <div>
          <p>Searched words: {searchResults.join(", ")}</p>
          <ul>
            {searchResults.map((result, index) => (
              <li key={index}>{result}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
