import React, { useState } from "react";
import axios from "axios";

function App() {
  const [inputValue, setInputValue] = useState("");
  const [searchedWords, setSearchedWords] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [error, setError] = useState(null);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleSearch = async () => {
    try {
      const normalizedInput = inputValue.trim().toLowerCase();
      const response = await axios.get("/data.txt");
      const lines = response.data.split("\n");
      const matchingLines = lines.filter(
        (line) => line.trim().toLowerCase() === normalizedInput
      );
      if (matchingLines.length === 0) {
        setError("No results found");
        setSearchResults([]);
      } else {
        const formattedData = matchingLines.concat(
          lines.filter((line) => line !== matchingLines[0])
        );
        setSearchedWords(matchingLines);
        setSearchResults(formattedData);
        setError(null);
      }
    } catch (err) {
      console.error(err);
      setError("Failed to fetch data");
      setSearchResults([]);
    }
  };

  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      handleSearch();
    }
  };

  return (
    <div>
      <div>
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {error && <div>{error}</div>}
      {searchedWords.length > 0 && (
        <div>
          <p>Searched words: {searchedWords.join(", ")}</p>
        </div>
      )}
      {searchResults.length > 0 && (
        <div>
          <ul>
            {searchResults.map((line, index) => (
              <li key={index}>{line}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
