import React, { useState } from "react";
import axios from "axios";

function App() {
  const [searchWord, setSearchWord] = useState("");
  const [searchResults, setSearchResults] = useState(null);

  const handleSearch = async () => {
    const formattedSearchWord = searchWord.trim().toLowerCase();
    try {
      const response = await axios.get("/data.txt");
      const lines = response.data.split("\n");
      const results = [];
      for (const line of lines) {
        const words = line.split(" ");
        const formattedWords = words.map((word) => word.trim().toLowerCase());
        if (formattedWords.includes(formattedSearchWord)) {
          if (formattedWords[0] === formattedSearchWord) {
            results.unshift(line);
          } else {
            results.push(line);
          }
        }
      }
      if (results.length > 0) {
        setSearchResults(results);
      } else {
        setSearchResults(["No results found."]);
      }
    } catch (error) {
      setSearchResults(["An error occurred while searching."]);
    }
  };

  return (
    <div>
      <input
        type="text"
        value={searchWord}
        onChange={(event) => setSearchWord(event.target.value)}
        onKeyPress={(event) => {
          if (event.key === "Enter") {
            handleSearch();
          }
        }}
      />
      <button onClick={handleSearch}>Search</button>
      <ul>
        {searchResults &&
          searchResults.map((result, index) => <li key={index}>{result}</li>)}
      </ul>
    </div>
  );
}

export default App;
